mapper.select:
		SELECT board_id,
		       title,
		       CONVERT(contents USING utf8) as contents,
		       reg_dt,
		       view_cnt 
		  FROM mhnb01001tb
		  
		  	
				board_id = #{board_id }
			
			
				
				title like #{val }
				
				
				contents like #{val }
				
				
				title like #{val } or contents like #{val }
				
			
		  
		  
		  	
		  		
		  		reg_dt ASC
		  		
		  		
		  		view_cnt DESC
		  		
		  		
		  		view_cnt ASC
		  		
		  		
		  		reg_dt DESC
		  		






		  	
		  
		  LIMIT #{legnth} OFFSET #{offset}
