mapper.select:
		SELECT a.type_cd, a.type_nm, b.news_id, b.score, b.reg_id
   		  FROM mhnc91001tb a
   		  LEFT JOIN ( SELECT news_id, type_cd, score, reg_id 
                 		FROM mhnd01001tb 
               		   WHERE news_id = #{news_id}
               		     AND reg_id = #{reg_id} 
               		) AS b
	  			 ON a.type_cd = b.type_cd
